import React, {useState} from "react";
import MealCalendar from "./MealCalendar";
import "./styles.css";

function App() {
  const axios = require('axios').default;
  const [dishes, setDishes] = useState("");
  const [mealPlan, setMealPlan] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    axios.post('http://127.0.0.1:5000/generate_meal_plan', {
      "Content-Type": "application/json"
    }).then(function(response){
      console.log(response)
    }) 
    try {
      const response = await fetch("http://127.0.0.1:5000/generate_meal_plan", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ dishes: dishes.split(",") }),
      });
      const data = await response.json();
      if (response.ok) {
        setMealPlan(data.meal_plan);
      } else {
        console.error(data.error);
      }
    } catch (error) {
      console.error("Error:", error);
    }
  };

  return (
    <div className="App">
      <h1>Weekly Meal Planner</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Enter Dishes (comma-separated):
          <input
            type="text"
            value={dishes}
            onChange={(e) => setDishes(e.target.value)}
            placeholder="e.g., Pizza, Salad, Soup"
          />
        </label>
        <button type="submit">Generate Meal Plan</button>
      </form>
      {mealPlan && <MealCalendar mealPlan={mealPlan} />}
    </div>
  );
}

export default App;
